mapper.update:
UPDATE t_mall_auction_bidding SET auc_status = #{aucStatus,jdbcType=TINYINT},
    order_id = #{orderId,jdbcType=INTEGER},
    order_detail_id = #{orderDetailId,jdbcType=INTEGER}
WHERE auc_id = #{aucId,jdbcType=INTEGER} AND pro_id = #{proId,jdbcType=INTEGER}
      AND user_id = #{userId,jdbcType=INTEGER}

mapper.select:
SELECT count(d.det_pro_num) AS cont
FROM t_mall_auction_bidding j
LEFT JOIN t_mall_order_detail d ON j.order_detail_id=d.id
WHERE j.user_id = #{joinUserId}
AND j.auc_id=#{aucId}

select

from t_mall_auction_bidding b
where auc_id = #{aucId} and user_id=#{userId}

    and order_id=#{orderId}


    and pro_id = #{proId}

select b.*,a.auc_type,a.shop_id from t_mall_auction_bidding b left join t_mall_auction a on a.id=b.auc_id where b.auc_status = 1 and a.auc_type=1
and b.user_id=#{userId}
and
b.user_id = #{item,jdbcType=INTEGER}
union select b.*,a.auc_type,a.shop_id from t_mall_auction_bidding b left join t_mall_auction a on a.id=b.auc_id where a.auc_type=2 and a.auc_start_time < now() and a.auc_end_time <= now()
and b.user_id=#{userId}
and
b.user_id = #{item,jdbcType=INTEGER}
order by create_time desc

select b.*,a.auc_type,a.shop_id from t_mall_auction_bidding b left join t_mall_auction a on a.id=b.auc_id where a.auc_start_time <= now() and now() < a.auc_end_time
and b.user_id = #{memberId}
and
b.user_id = #{item,jdbcType=INTEGER}

select 
from t_mall_auction_bidding b
where b.auc_id = #{aucId}

    and b.user_id=#{userId}

and b.auc_status >= 0
order by b.auc_price desc
