mapper.select:
select
from t_mall_group_buy g left join t_mall_group_join j on g.id = j.group_buy_id and j.join_type = 1 left join t_mall_product p on p.id= g.product_id where g.is_delete = 0
and g.g_start_time > now() and g.g_end_time > now()
and g.g_start_time <= now() and now() < g.g_end_time
and g.g_start_time < now() and g.g_end_time <= now()
and g.shop_id in
#{item.id,jdbcType=INTEGER}
and g.shop_id = #{shoplist[0]}
group by g.id order by g.create_time desc limit #{firstNum},#{maxNum}

SELECT distinct(a.id) as id,a.pro_price,a.pro_name,a.is_specifica,c.image_url,e.specifica_img_url,d.inv_price,a.pro_stock_total as stockTotal, a.create_time as createTime,d.id as invenId,a.pro_type_id,a.erp_pro_id,a.erp_inv_id,a.shop_id from t_mall_product a LEFT JOIN (SELECT ass_id,image_url from t_mall_image_associative WHERE ass_type=1 AND is_delete=0 AND is_main_images=1) c ON a.id=c.ass_id LEFT JOIN (SELECT product_id,specifica_img_id,inv_num,inv_price,id FROM t_mall_product_inventory where is_default=1 AND is_delete=0)d ON a.id=d.product_id LEFT JOIN t_mall_product_specifica e ON d.specifica_img_id=e.id WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0
AND a.shop_id = #{shopId,jdbcType=INTEGER}
AND a.pro_name like '%${proName}%'
AND a.id in (select product_id from t_mall_product_group where group_id = #{groupId})
and
a.shop_id = #{item.id,jdbcType=INTEGER}
order by a.pro_type_id ,a.id desc limit #{firstNum},#{maxNum}

SELECT count(distinct(a.id)) from t_mall_product a WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0
AND a.shop_id = #{shopId,jdbcType=INTEGER}
AND a.pro_name like '%${proName}%'
AND a.id in (select product_id from t_mall_product_group where group_id = #{groupId})
and
a.shop_id = #{item.id,jdbcType=INTEGER}

SELECT
from t_mall_group_buy g left join t_mall_group_join j on g.id = j.group_buy_id WHERE g.is_delete = 0 and g.is_use =1
and g.product_id = #{productId}
and ((g_start_time <= now() and now() < g_end_time ) or ( g_start_time > now() and g_end_time > now())
and g.id != #{id}
)
and j.id>0
group by g.id

SELECT a.id,a.is_member_discount,a.pro_price,a.pro_name,a.is_specifica,a.is_specifica,c.image_url,d.specifica_img_id,e.specifica_img_url,d.inv_num,d.inv_price, gb.id as gBuyId,gb.g_price as price ,gb.g_start_time as startTime ,gb.g_end_time as endTime,gb.g_people_num as peopleNum,a.shop_id as shopId,d.id as inv_id, a.return_day, gb.g_max_buy_num as maxNum from t_mall_group_buy gb left join t_mall_product a on a.id = gb.product_id and gb.shop_id = a.shop_id LEFT JOIN (SELECT ass_id,image_url from t_mall_image_associative WHERE ass_type=1 AND is_delete=0 AND is_main_images=1) c ON a.id=c.ass_id LEFT JOIN (SELECT product_id,specifica_img_id,inv_num,inv_price,id FROM t_mall_product_inventory where is_default=1 AND is_delete=0)d ON a.id=d.product_id LEFT JOIN t_mall_product_specifica e ON d.specifica_img_id=e.id left join t_mall_product_group pg on pg.product_id=a.id and pg.is_delete=0 left join t_mall_group g on g.id=pg.group_id and g.is_delete=0 WHERE gb.is_delete=0 and gb.is_use=1
and a.is_publish=1 AND a.check_status=1 AND a.is_delete=0
and gb.g_start_time <= now() and now() < gb.g_end_time
and gb.id=#{id,jdbcType=INTEGER}
and gb.shop_id=#{shopId,jdbcType=INTEGER}
and ( a.pro_name like CONCAT("%","${proName}","%") or g.group_name like CONCAT("%","${proName}","%") )
and gb.product_id=#{productId,jdbcType=INTEGER}
and pg.group_id = #{groupId,jdbcType=INTEGER}
GROUP BY a.id
order by gb.id
order by a.pro_sale_total+a.sales_base
order by gb.g_price
desc

select

from t_mall_group_buy gb
where gb.is_delete = 0 and gb.is_use = 1
and gb.product_id=#{productId,jdbcType=INTEGER}
and gb.shop_id=#{shopId,jdbcType=INTEGER}

    and gb.id = #{id}


    and ((gb.g_start_time <= now() and now() < gb.g_end_time ) or (gb.g_start_time > now() and gb.g_end_time > now()))
    limit 1

SELECT count(distinct(gb.id)) from t_mall_group_buy gb left join t_mall_product a on a.id = gb.product_id and gb.shop_id = a.shop_id WHERE gb.is_delete=0 and gb.is_use=1
and a.is_publish=1 AND a.check_status=1 AND a.is_delete=0
AND ((gb.g_start_time <= NOW() AND NOW() < gb.g_end_time) OR (gb.g_start_time > NOW() AND gb.g_end_time > NOW()))
and gb.product_id=#{productId,jdbcType=INTEGER}
and gb.shop_id=#{shopId,jdbcType=INTEGER}

SELECT a.id,a.is_member_discount,a.pro_price,a.pro_name,a.is_specifica,a.is_specifica, a.shop_id , a.return_day,a.pro_sale_total,a.sales_base, gb.id AS activityId,gb.g_price AS price ,gb.g_start_time AS startTime ,gb.g_end_time AS endTime,gb.g_people_num AS peopleNum FROM t_mall_group_buy gb LEFT JOIN t_mall_product a ON a.id = gb.product_id AND gb.shop_id = a.shop_id
LEFT JOIN t_mall_product_group pg ON pg.product_id=a.id AND pg.is_delete=0
/*LEFT JOIN t_mall_group g ON g.id=pg.group_id AND g.is_delete=0*/ WHERE gb.is_delete=0 AND gb.is_use=1
and gb.user_id = #{busId}
and gb.shop_id=#{shopId,jdbcType=INTEGER}
and a.is_publish=1 AND a.check_status=1 AND a.is_delete=0 and gb.g_start_time <= now() and now() < gb.g_end_time
/*and ( a.pro_name like CONCAT("%","${searchContent}","%") or g.group_name like CONCAT("%","${searchContent}","%") )*/ and a.pro_name like CONCAT("%","${searchContent}","%")
and pg.group_id = #{groupId,jdbcType=INTEGER}
GROUP BY a.id
order by gb.id
order by a.pro_sale_total+a.sales_base
order by gb.g_price
desc
limit #{firstNum},#{maxNum}

