mapper.select:
SELECT a.id,a.htmlname,a.creattime,a.codeUrl,a.bakurl,a.state,bus_user_id
FROM t_mall_html a
WHERE is_delete = 0 and a.source_type=2 and a.bus_user_id=#{user_id}

    OR a.pid=#{user_id}

limit #{firstNum},#{pageSize}

SELECT a.id,a.htmlname,a.creattime,a.codeUrl,a.bakurl,a.state FROM t_mall_html a
WHERE a.is_delete = 0 and a.source_type=1 and a.state=0
limit #{firstNum},#{pageSize}

select count(a.id)
from t_mall_html a
WHERE a.is_delete = 0 and a.source_type=2 and a.bus_user_id=#{user_id}

    OR a.pid=#{user_id}

SELECT a.id,a.htmlname,a.creattime,a.codeUrl,a.bakurl,a.state,bus_user_id,introduce
FROM t_mall_html a
WHERE a.is_delete = 0 and a.source_type=2 and a.bus_user_id=#{user_id}

    OR a.pid=#{user_id}

limit #{firstNum},#{maxNum}

SELECT

FROM t_mall_html a
WHERE is_delete = 0

    and a.htmlname like CONCAT(CONCAT('%', #{htmlName}),'%')


    and(
    a.pid in

        #{id}

    AND a.pid>0
    or a.bus_user_id in

        #{id}

    )

ORDER BY source_type ASC
limit #{firstNum},#{maxNum}

