mapper.select:
select
from t_mall_integral i left join t_mall_product p on p.id= i.product_id and p.is_delete=0 where i.is_delete = 0 and p.is_delete=0 and p.is_publish=1 and p.check_status=1
and i.start_time > now() and i.end_time > now() and i.is_use=1
and i.start_time <= now() and now() < i.end_time and i.is_use=1
and i.start_time < now() and i.end_time <= now() and i.is_use=1
and i.is_use= -1
and
i.shop_id = #{item.id,jdbcType=INTEGER}
and i.shop_id=#{shopId }
group by i.id order by i.create_time desc limit #{firstNum},#{maxNum}

select

from t_mall_integral

    and is_delete = 0

        and product_id=#{productId}


        and user_id =#{userId}


        and shop_id=#{shopId}


        and is_use=1

SELECT DISTINCT(a.id) as id,a.pro_name,i.`money`,i.shop_id,i.user_id as busId,i.product_id
FROM t_mall_integral i
LEFT JOIN t_mall_product a ON i.`product_id`=a.`id` AND a.is_publish=1 AND a.check_status=1 AND a.is_delete=0
WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0 AND i.`is_delete`=0 and i.is_use =1
and i.start_time <= now() and now() < i.end_time

    and

        a.shop_id = #{item.id,jdbcType=INTEGER}



    and a.shop_id=#{shopId}

order by i.id desc

    limit #{firstNum},#{maxNum}

SELECT count(DISTINCT a.id) FROM t_mall_integral i LEFT JOIN t_mall_product a ON i.`product_id`=a.`id` AND a.is_publish=1 AND a.check_status=1 AND a.is_delete=0 WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0 AND i.`is_delete`=0 and i.is_use =1 and i.start_time <= now() and now() < i.end_time
and
a.shop_id = #{item.id,jdbcType=INTEGER}
and a.shop_id=#{shopId}

SELECT COUNT(p.id) FROM t_mall_integral s
LEFT JOIN t_mall_product p ON p.`id` = s.`product_id`
WHERE s.is_delete=0 AND p.is_delete=0 AND p.is_publish=1 AND p.check_status=1 AND s.start_time <= NOW() AND NOW() < s.end_time  AND s.shop_id=#{shopId}
