mapper.select:
SELECT a.id, a.pag_name, a.pag_descript, a.pag_type_id, a.pag_css, a.pag_data, a.pag_user_id, a.pag_sto_id, a.pag_create_time, a.pag_is_main, a.pag_html, a.codeUrl FROM t_mall_page a LEFT JOIN t_mall_store b ON a.pag_sto_id=b.id WHERE b.is_delete=0
AND pag_sto_id IN
#{item.id}
and a.pag_name like '%${pagName}%'
limit #{firstResult},#{maxResult}

SELECT p.id
FROM t_mall_page p
LEFT JOIN t_mall_store s ON s.id=p.`pag_sto_id`
WHERE p.pag_is_main=1 AND s.`is_delete`=0

    and s.wx_shop_id = #{wxShopId}


    and p.pag_user_id = #{userId}

SELECT a.id,a.pag_sto_id FROM t_mall_page a
WHERE a.pag_is_main=1 AND a.pag_user_id=#{userId}

    AND a.pag_sto_id IN

        #{item.id}
