mapper.select:
select
from t_mall_pifa p left join t_mall_store s on p.shop_id = s.id left join t_mall_product mp on mp.id= p.product_id where p.is_delete = 0 and mp.is_delete = 0
and p.is_use = -1
and p.pf_start_time > now() and p.pf_end_time > now()
and p.pf_start_time <= now() and now() < p.pf_end_time
and p.pf_start_time < now() and p.pf_end_time <= now()
and p.shop_id = #{shopId}
and
p.shop_id = #{item.id,jdbcType=INTEGER}
group by p.id order by p.create_time desc limit #{firstNum},#{maxNum}

SELECT p.id, p.product_id, pf_price, pf_start_time, pf_end_time, p.user_id, p.shop_id, p.create_time, p.is_delete, p.is_use,p.pf_type
from t_mall_pifa p
left join t_mall_order o on p.id=o.group_buy_id and o.order_type=7
WHERE p.is_delete = 0 and p.is_use =1

    and p.product_id = #{productId}

and ((p.pf_start_time <= now() and now() < p.pf_end_time ) or
(p.pf_start_time > now() and p.pf_end_time > now())

    and p.id != #{id}

) and o.id > 0

SELECT p.id, p.product_id, pf_price, pf_start_time, pf_end_time, p.user_id, p.shop_id, p.create_time, p.is_delete, p.is_use,p.pf_type from t_mall_pifa p WHERE p.is_delete = 0 and p.is_use =1
and p.product_id = #{productId}
and ((p.pf_start_time <= now() and now() < p.pf_end_time ) or (p.pf_start_time > now() and p.pf_end_time > now()))
and p.id != #{id}

SELECT gb.id,gb.is_member_discount,gb.pro_price,gb.pro_cost_price,gb.pro_name,gb.is_specifica, gb.pifaId,gb.pf_price AS pfPrice ,gb.pf_start_time AS startTime ,gb.pf_end_time AS endTime,gb.shop_id AS shopId, gb.return_day,gb.pro_stock_total AS stockTotal,gb.pf_type,d.specifica_img_id,d.inv_num,d.inv_price,d.id AS inv_id FROM ( SELECT a.id,a.is_member_discount,a.pro_price,a.pro_cost_price,a.pro_name,a.is_specifica, gb.id AS pifaId,gb.pf_price ,gb.pf_start_time ,gb.pf_end_time ,a.shop_id , a.return_day,a.pro_stock_total ,gb.pf_type from t_mall_pifa gb left join t_mall_product a on a.id = gb.product_id and gb.shop_id = a.shop_id
left join t_mall_product_group pg on pg.product_id=a.id and pg.is_delete=0
WHERE gb.is_delete=0 and gb.is_use=1
and a.is_publish=1 AND a.check_status=1 AND a.is_delete=0
and ((gb.pf_start_time <= now() and now() < gb.pf_end_time ) or (gb.pf_start_time > now() and gb.pf_end_time > now()))
and gb.id=#{id,jdbcType=INTEGER}
and gb.shop_id=#{shopId,jdbcType=INTEGER}
and a.pro_name like CONCAT("%","${proName}","%")
and gb.product_id=#{productId,jdbcType=INTEGER}
and pg.group_id = #{groupId,jdbcType=INTEGER}
group by gb.id
order by gb.id
order by a.pro_sale_total
order by gb.pf_price
desc
limit #{firstNum},#{maxNum}
) gb LEFT JOIN (SELECT product_id,specifica_img_id,inv_num,inv_price,id FROM t_mall_product_inventory WHERE is_default=1 AND is_delete=0)d ON gb.id=d.product_id

SELECT count(gb.id)
from t_mall_pifa gb
left join t_mall_product a on a.id = gb.product_id and gb.shop_id = a.shop_id

    left join t_mall_product_group pg on pg.product_id=a.id and pg.is_delete=0

WHERE gb.is_delete=0 and gb.is_use=1

    and a.is_publish=1 AND a.check_status=1 AND a.is_delete=0


    and ((gb.pf_start_time <= now() and now() < gb.pf_end_time ) or (gb.pf_start_time > now() and gb.pf_end_time > now()))


    and gb.id=#{id,jdbcType=INTEGER}


    and gb.shop_id=#{shopId,jdbcType=INTEGER}


    and a.pro_name like CONCAT("%","${proName}","%")


    and gb.product_id=#{productId,jdbcType=INTEGER}


    and pg.group_id = #{groupId,jdbcType=INTEGER}

select

from t_mall_pifa p
where p.is_delete = 0 and p.is_use = 1

    and p.product_id=#{productId}


    and p.shop_id=#{shopId}


    and p.id = #{id}


    and ((p.pf_start_time <= now() and now() < p.pf_end_time ) or (p.pf_start_time > now() and p.pf_end_time > now()))
    limit 1

SELECT gb.id,gb.is_member_discount,gb.pro_price,gb.pro_name,gb.is_specifica,gb.shop_id,gb.pro_cost_price, gb.activityId,gb.pf_price AS price ,gb.pf_start_time AS startTime ,gb.pf_end_time AS endTime, gb.return_day,gb.pf_type,gb.pro_sale_total,gb.sales_base FROM ( SELECT a.id,a.is_member_discount,a.pro_price,a.pro_cost_price,a.pro_name,a.is_specifica,a.pro_sale_total,a.sales_base, gb.id AS activityId,gb.pf_price ,gb.pf_start_time ,gb.pf_end_time ,a.shop_id , a.return_day,a.pro_stock_total ,gb.pf_type from t_mall_pifa gb left join t_mall_product a on a.id = gb.product_id and gb.shop_id = a.shop_id
left join t_mall_product_group pg on pg.product_id=a.id and pg.is_delete=0
WHERE gb.is_delete=0 and gb.is_use=1 and a.is_publish=1 AND a.check_status=1 AND a.is_delete=0
and gb.user_id = #{busId}
and ((gb.pf_start_time <= now() and now() < gb.pf_end_time ) or (gb.pf_start_time > now() and gb.pf_end_time > now()))
and gb.shop_id=#{shopId,jdbcType=INTEGER}
and a.pro_name like CONCAT("%","${searchContent}","%")
and pg.group_id = #{groupId,jdbcType=INTEGER}
group by gb.id
order by gb.id
order by a.pro_sale_total
order by gb.pf_price
desc
limit #{firstNum},#{maxNum}
) gb LEFT JOIN (SELECT product_id,specifica_img_id,inv_num,inv_price,id FROM t_mall_product_inventory WHERE is_default=1 AND is_delete=0)d ON gb.id=d.product_id

SELECT count(distinct a.id) from t_mall_pifa gb left join t_mall_product a on a.id = gb.product_id and gb.shop_id = a.shop_id
left join t_mall_product_group pg on pg.product_id=a.id and pg.is_delete=0
WHERE gb.is_delete=0 and gb.is_use=1 and a.is_publish=1 AND a.check_status=1 AND a.is_delete=0
and gb.user_id = #{busId}
and ((gb.pf_start_time <= now() and now() < gb.pf_end_time ) or (gb.pf_start_time > now() and gb.pf_end_time > now()))
and gb.shop_id=#{shopId,jdbcType=INTEGER}
and a.pro_name like CONCAT("%","${searchContent}","%")
and pg.group_id = #{groupId,jdbcType=INTEGER}
