mapper.select:
SELECT p.id, p.product_id, pf_price, pf_start_time, pf_end_time, p.user_id, p.shop_id, p.create_time, p.is_delete, p.is_use,p.pf_type
from t_mall_pifa  p
left join t_mall_order o on p.id=o.group_buy_id and o.order_type=7
WHERE p.is_delete = 0 and p.is_use  =1

    and p.product_id = #{productId}

and ((p.pf_start_time <= now() and now() < p.pf_end_time ) or
(p.pf_start_time > now() and  p.pf_end_time > now())

    and p.id != #{id}

) and o.id > 0

SELECT p.id, p.product_id, pf_price, pf_start_time, pf_end_time, p.user_id, p.shop_id, p.create_time, p.is_delete, p.is_use,p.pf_type from t_mall_pifa p WHERE p.is_delete = 0 and p.is_use =1
and p.product_id = #{productId}
and ((p.pf_start_time <= now() and now() < p.pf_end_time ) or (p.pf_start_time > now() and p.pf_end_time > now()))
and p.id != #{id}

SELECT a.id,a.is_member_discount,a.pro_price,a.pro_cost_price,a.pro_name,a.is_specifica,a.is_specifica,c.image_url,d.specifica_img_id,e.specifica_img_url,d.inv_num,d.inv_price, gb.id as pifaId,gb.pf_price as pfPrice ,gb.pf_start_time as startTime ,gb.pf_end_time as endTime,a.shop_id as shopId,d.id as inv_id, a.return_day,a.pro_stock_total as stockTotal,gb.pf_type from t_mall_pifa gb left join t_mall_product a on a.id = gb.product_id and gb.shop_id = a.shop_id LEFT JOIN (SELECT ass_id,image_url from t_mall_image_associative WHERE ass_type=1 AND is_delete=0 AND is_main_images=1) c ON a.id=c.ass_id LEFT JOIN (SELECT product_id,specifica_img_id,inv_num,inv_price,id FROM t_mall_product_inventory where is_default=1 AND is_delete=0)d ON a.id=d.product_id LEFT JOIN t_mall_product_specifica e ON d.specifica_img_id=e.id left join t_mall_product_group pg on pg.product_id=a.id and pg.is_delete=0 left join t_mall_group g on g.id=pg.group_id and g.is_delete=0 WHERE gb.is_delete=0 and gb.is_use=1
and a.is_publish=1 AND a.check_status=1 AND a.is_delete=0
and ((gb.pf_start_time <= now() and now() < gb.pf_end_time ) or (gb.pf_start_time > now() and gb.pf_end_time > now()))
and gb.id=#{id,jdbcType=INTEGER}
and gb.shop_id=#{shopId,jdbcType=INTEGER}
and (a.pro_name like CONCAT("%","${proName}","%") or g.group_name like CONCAT("%","${proName}","%") )
and gb.product_id=#{productId,jdbcType=INTEGER}
and pg.group_id = #{groupId,jdbcType=INTEGER}
group by gb.id
order by gb.id
order by a.pro_sale_total
order by gb.pf_price
desc
