mapper.select:
select
from t_mall_presale g left join t_mall_product p on p.id= g.product_id and p.is_delete=0 where g.is_delete = 0
and g.is_use = -1
and g.sale_start_time > now() and g.sale_end_time > now()
and g.sale_start_time <= now() and now() < g.sale_end_time
and g.sale_start_time < now() and g.sale_end_time <= now()
and p.shop_id =#{shopId}
and
g.shop_id = #{item.id,jdbcType=INTEGER}
group by g.id order by g.create_time desc limit #{firstNum},#{maxNum}

SELECT
from t_mall_presale g left join t_mall_store s on g.shop_id = s.id left join t_mall_product p on p.id= g.product_id and p.is_delete=0 WHERE g.is_delete = 0 and g.id = #{id}
group by g.id

select

from t_mall_presale g
where g.is_delete = 0 and g.is_use = 1

    and g.product_id=#{productId}


    and g.shop_id=#{shopId}


    and g.id=#{id}


    and (g.sale_start_time <= now() and now() < g.sale_end_time or g.sale_start_time > now() and g.sale_end_time > now())

limit 1

SELECT a.id,a.is_member_discount,a.pro_price,a.pro_cost_price,a.pro_name,a.is_specifica,c.image_url,e.specifica_img_url,d.inv_num,d.inv_price, a.shop_id as shopId,d.id as inv_id,a.pro_stock_total as stockTotal, gb.id as preId, gb.shop_id, gb.sale_start_time, gb.sale_end_time, gb.is_deposit, gb.deposit_percent from t_mall_presale gb left join t_mall_product a on a.id = gb.product_id and gb.shop_id = a.shop_id LEFT JOIN (SELECT ass_id,image_url from t_mall_image_associative WHERE ass_type=1 AND is_delete=0 AND is_main_images=1) c ON a.id=c.ass_id LEFT JOIN (SELECT product_id,specifica_img_id,inv_num,inv_price,id FROM t_mall_product_inventory where is_default=1 AND is_delete=0)d ON a.id=d.product_id LEFT JOIN t_mall_product_specifica e ON d.specifica_img_id=e.id left join t_mall_product_group pg on pg.product_id=a.id and pg.is_delete=0 left join t_mall_group g on g.id=pg.group_id and g.is_delete=0 WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0 and gb.is_delete=0 and gb.is_use=1
and ((gb.sale_start_time <= now() and now() < gb.sale_end_time ) or (gb.sale_start_time > now() and gb.sale_end_time > now()))
and gb.id=#{id,jdbcType=INTEGER}
and gb.shop_id=#{shopId,jdbcType=INTEGER}
and ( a.pro_name like CONCAT("%","${proName}","%") or g.group_name like CONCAT("%","${proName}","%") )
and gb.product_id=#{productId,jdbcType=INTEGER}
and pg.group_id = #{groupId,jdbcType=INTEGER}
group by gb.id
order by gb.id
order by a.pro_sale_total
order by a.pro_price
desc

SELECT count(gb.id) from t_mall_presale gb left join t_mall_product a on a.id = gb.product_id and gb.shop_id = a.shop_id WHERE gb.is_delete=0 and gb.is_use=1
and a.is_publish=1 AND a.check_status=1 AND a.is_delete=0
and ((gb.sale_start_time <= now() and now() < gb.sale_end_time ) or (gb.sale_start_time > now() and gb.sale_end_time > now()))
and gb.product_id=#{productId,jdbcType=INTEGER}
and gb.shop_id=#{shopId,jdbcType=INTEGER}

SELECT mp.id,mp.pro_price,mp.pro_name AS proName,mp.is_specifica,b.product_introdu AS product_introdu,c.image_url,d.inv_price,mp.shop_id,mp.user_id, mp.sale_start_time,mp.sale_end_time,mp.preId,mp.order_num as orderNum from ( select DISTINCT(a.id) as id,a.pro_price,a.pro_name ,a.is_specifica,mp.shop_id,mp.user_id, mp.sale_start_time,mp.sale_end_time,mp.id AS preId,mp.order_num FROM t_mall_presale mp LEFT JOIN t_mall_product a ON a.id=mp.product_id WHERE a.shop_id=#{shopId} AND a.is_publish=1 AND a.check_status=1 AND a.is_delete=0 AND ((mp.sale_start_time > NOW() AND mp.sale_end_time > NOW() ) OR (mp.sale_start_time <= NOW() AND NOW() < mp.sale_end_time)) AND mp.is_use=1 AND mp.is_delete=0
and a.pro_name like '%${searchName}%'
limit #{firstNum},#{maxNum}
) as mp LEFT JOIN t_mall_product_detail b ON mp.id=b.product_id LEFT JOIN (SELECT ass_id,image_url FROM t_mall_image_associative WHERE ass_type=1 AND is_delete=0 AND is_main_images=1) c ON mp.id=c.ass_id LEFT JOIN (SELECT product_id,specifica_img_id,inv_num,inv_price FROM t_mall_product_inventory WHERE is_default=1 AND is_delete=0)d ON mp.id=d.product_id

SELECT a.id,a.is_member_discount,a.pro_price,a.pro_name,a.is_specifica, a.shop_id , a.return_day,a.pro_sale_total,a.sales_base,
a.activityId, a.startTime, a.endTime, d.inv_price
from (
select a.id,a.is_member_discount,a.pro_price,a.pro_name,a.is_specifica, a.shop_id, a.return_day,a.pro_sale_total,a.sales_base,
gb.id as activityId, gb.sale_start_time as startTime, gb.sale_end_time as endTime
from t_mall_presale gb
left join t_mall_product a on a.id = gb.product_id and gb.shop_id = a.shop_id

    left join t_mall_product_group pg on pg.product_id=a.id and pg.is_delete=0

WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0 and gb.is_delete=0 and gb.is_use=1

    and gb.user_id = #{busId}


    and gb.shop_id=#{shopId,jdbcType=INTEGER}


    and pg.group_id = #{groupId,jdbcType=INTEGER}

and ((gb.sale_start_time <= now() and now() < gb.sale_end_time ) or (gb.sale_start_time > now() and gb.sale_end_time > now()))

    and a.pro_name like CONCAT("%","${searchContent}","%")
    /*and ( a.pro_name like CONCAT("%","${searchContent}","%") or g.group_name like CONCAT("%","${searchContent}","%") )*/

group by gb.id


        order by gb.id


        order by a.pro_sale_total+a.sales_base


        desc


        limit #{firstNum},#{maxNum}


) as a

LEFT JOIN (SELECT product_id,specifica_img_id,inv_num,inv_price,id FROM t_mall_product_inventory where is_default=1 AND is_delete=0)d ON a.id=d.product_id


    ORDER BY IF(d.inv_price>0 ,d.inv_price,a.pro_price )

        desc


        limit #{firstNum},#{maxNum}

select count(a.id)
from t_mall_presale gb
left join t_mall_product a on a.id = gb.product_id and gb.shop_id = a.shop_id

    left join t_mall_product_group pg on pg.product_id=a.id and pg.is_delete=0

WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0 and gb.is_delete=0 and gb.is_use=1

    and gb.user_id = #{busId}


    and gb.shop_id=#{shopId,jdbcType=INTEGER}


    and pg.group_id = #{groupId,jdbcType=INTEGER}

and ((gb.sale_start_time <= now() and now() < gb.sale_end_time ) or (gb.sale_start_time > now() and gb.sale_end_time > now()))

    /*and ( a.pro_name like CONCAT("%","${searchContent}","%") or g.group_name like CONCAT("%","${searchContent}","%") )*/
    and a.pro_name like CONCAT("%","${searchContent}","%")
