SELECT COUNT(DISTINCT a.id) as counts,pg.group_id as groupId
FROM t_mall_product a
LEFT JOIN t_mall_product_group pg ON pg.product_id=a.id AND pg.is_delete=0
WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0
and a.user_id=#{userId}

    AND pg.group_id = #{groupIds}


    AND pg.group_id in

        #{item}


GROUP BY pg.group_id

select count(p.id)
from t_mall_product p
where p.is_delete=0 and p.is_mall_show=0

    and p.user_id = #{userId,jdbcType=INTEGER}


    and p.is_publish = #{isPublish}


    and p.is_publish in

        #{id}



    and p.pro_stock_total = #{proStockTotal}


    and p.pro_name like '%${proName}%'


    and p.check_status = #{checkStatus}


    and p.shop_id = #{shopId}


    and

        p.shop_id = #{item.id,jdbcType=INTEGER}

select p.id as id, i.image_url as imageUrl,p.pro_name as proName, p.pro_price as proPrice,p.pro_stock_total as
stockTotal, p.pro_sale_total as saleTotal,p.create_time as createTime, p.is_publish as isPublish , p.check_status as
checkStatus, p.check_reason as checkReason,p.shop_id as shopId ,p.two_code_path as twoCodePath,p.return_day as returnDay,
views_num as viewsNum, is_show_views as isShowView, is_integral_deduction as isIntegralDeduction,is_fenbi_deduction as isFenbiDeduction,p.is_specifica as isSpecifica,
p.sales_base as salesBase ,p.erp_pro_id as erpProId,p.erp_inv_id as erpInvId,s.wx_shop_id as wxShopId,p.user_id as userId
from t_mall_product p
left join t_mall_image_associative i on p.id=i.ass_id and i.is_main_images=1 and i.is_delete=0 and i.ass_type =
1
left join t_mall_store s on p.shop_id = s.id
where p.is_delete=0 and p.is_mall_show=0

    and p.user_id = #{userId,jdbcType=INTEGER}


    and p.is_publish = #{isPublish}


    and p.is_publish in

        #{id}



    and p.pro_stock_total = #{proStockTotal}


    and p.pro_name like '%${proName}%'


    and p.check_status = #{checkStatus}


    and p.shop_id = #{shopId}


    and

        p.shop_id = #{item.id,jdbcType=INTEGER}


order by p.create_time desc
limit #{firstNum},#{maxNum}

select p.id as id, p.pro_name as proName, p.pro_price as proPrice,p.pro_stock_total as stockTotal ,p.pro_type_id as proTypeId,
p.pro_sale_total as saleTotal,p.create_time as createTime, p.is_publish as isPublish, p.check_status as checkStatus,
p.check_reason as checkReason,p.shop_id as shopId, p.is_specifica as isSpecifica, p.is_platform_check as isPlatFormCheck,p.user_id as userId
from t_mall_product p
where p.is_delete=0 and p.is_mall_show=0

    and p.user_id in

        #{id}



    and p.check_status = #{checkStatus}

order by p.create_time desc
limit #{firstNum},#{maxNum}

select p.id as id, i.image_url,p.pro_name, p.pro_price ,p.pro_cost_price,p.pro_stock_total ,p.shop_id, p.is_specifica,p.is_integral_deduction,p.is_fenbi_deduction,p.is_coupons,p.is_member_discount,p.pro_restriction_num from t_mall_product p left join t_mall_image_associative i on p.id=i.ass_id left join t_mall_store s on p.shop_id = s.id where i.is_main_images=1 and i.is_delete=0 and p.is_delete=0 and p.check_status=1 and p.is_publish=1 and i.ass_type = 1 and s.is_delete=0
and s.wx_shop_id=#{wxShopId}
order by p.create_time desc
limit #{firstNum},#{maxNum}

select p.id as id, i.image_url,p.pro_name, p.pro_price ,p.pro_cost_price,p.pro_stock_total ,p.shop_id,p.template_id p.is_specifica,p.is_integral_deduction,p.is_fenbi_deduction,p.is_coupons,p.is_member_discount,p.pro_restriction_num from t_mall_product p left join t_mall_image_associative i on p.id=i.ass_id left join t_mall_store s on p.shop_id = s.id where i.is_main_images=1 and i.is_delete=0 and p.is_delete=0 and p.check_status=1 and p.is_publish=1 and i.ass_type = 1 and s.is_delete=0
and s.wx_shop_id=#{wxShopId}
and p.id=#{productId}
order by p.create_time desc
limit #{firstNum},#{maxNum}

SELECT a.id,a.pro_price,a.pro_name,a.is_specifica,c.image_url,e.specifica_img_url,d.inv_num,d.inv_price,d.specifica_img_id
from (
SELECT DISTINCT(a.id),a.pro_price,a.pro_name,a.is_specifica,a.is_publish,a.check_status,a.is_delete
FROM t_mall_product a


    LEFT JOIN t_mall_product_group pg ON pg.product_id=a.id AND pg.is_delete=0


WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0


    and a.shop_id=#{shopId}


    and a.pro_name like '%${proName}%'


    and pg.group_id = #{groupId}


    limit #{firstNum},#{maxNum}

) a
LEFT JOIN (SELECT ass_id,image_url from t_mall_image_associative WHERE ass_type=1 AND is_delete=0 AND is_main_images=1) c ON a.id=c.ass_id
LEFT JOIN (SELECT product_id,specifica_img_id,inv_num,inv_price FROM t_mall_product_inventory where is_default=1 AND is_delete=0)d ON a.id=d.product_id
LEFT JOIN t_mall_product_specifica e ON d.specifica_img_id=e.id AND e.`is_delete`=0

SELECT count(a.id) from t_mall_product a
left join t_mall_product_group pg on pg.product_id=a.id and pg.is_delete=0
WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0
and a.shop_id=#{shopId}
and a.pro_name like '%${proName}%'
and pg.group_id = #{groupId}

SELECT a.id as id,a.shop_id,a.is_member_discount,a.pro_price,a.pro_name,a.is_specifica,a.change_integral,a.pro_cost_price,a.change_fenbi,a.pro_label,a.pro_type_id,a.member_type,a.user_id, a.pro_sale_total,a.sales_base ,d.inv_num,d.inv_price,d.specifica_img_id from ( select distinct a.id AS id,a.shop_id,a.is_member_discount,a.pro_price,a.pro_name,a.is_specifica,a.change_integral, a.pro_cost_price,a.change_fenbi,a.pro_label,a.pro_type_id,a.member_type,a.user_id,a.pro_sale_total,a.sales_base from t_mall_product a LEFT JOIN t_mall_product_group pg ON pg.product_id=a.id AND pg.is_delete=0 LEFT JOIN t_mall_group g ON g.id=pg.group_id AND g.is_delete=0
WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0 AND g.is_delete=0 AND pg.is_delete=0
and a.shop_id=#{shopId}
and (a.pro_name like '%${proName}%' or g.group_name like '%${proName}%')
and a.id=#{id}
and pg.group_id =#{groupId}
and (a.is_integral_change_pro = 1 and a.change_integral > 0)
and (a.is_fenbi_change_pro = 1 and a.change_fenbi > 0)
ORDER BY a.id
ORDER BY a.pro_sale_total+a.sales_base
ORDER BY a.change_integral
ORDER BY a.change_fenbi
desc
limit #{firstNum},#{maxNum}
) a LEFT JOIN (SELECT product_id,specifica_img_id,inv_num,inv_price FROM t_mall_product_inventory where is_default=1 AND is_delete=0) d ON a.id=d.product_id
ORDER BY IF(d.inv_price>0 ,d.inv_price,a.pro_price )
desc
limit #{firstNum},#{maxNum}

SELECT count(DISTINCT a.id) from t_mall_product a left join t_mall_product_group pg on pg.product_id=a.id and pg.is_delete=0 left join t_mall_group g on g.id=pg.group_id and g.is_delete=0 WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0 and g.is_delete=0 and pg.is_delete=0
and a.shop_id=#{shopId}
and (a.pro_name like '%${proName}%' or g.group_name like '%${proName}%')
and pg.group_id =#{groupId}
and (a.is_integral_change_pro = 1 and a.change_integral > 0)
and (a.is_fenbi_change_pro = 1 and a.change_fenbi > 0)
and
a.id = #{item,jdbcType=INTEGER}

SELECT a.id as id,a.shop_id,a.is_member_discount,a.pro_price,a.pro_name,a.is_specifica,a.change_integral,a.pro_cost_price,a.change_fenbi,a.pro_label,a.pro_type_id,a.member_type,a.user_id, a.pro_sale_total,a.sales_base ,d.inv_price from ( select distinct a.id AS id,a.shop_id,a.is_member_discount,a.pro_price,a.pro_name,a.is_specifica,a.change_integral, a.pro_cost_price,a.change_fenbi,a.pro_label,a.pro_type_id,a.member_type,a.user_id,a.pro_sale_total,a.sales_base from t_mall_product a LEFT JOIN t_mall_product_group pg ON pg.product_id=a.id AND pg.is_delete=0 LEFT JOIN t_mall_group g ON g.id=pg.group_id AND g.is_delete=0
WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0 AND g.is_delete=0 AND pg.is_delete=0
and a.user_id = #{busId}
and a.shop_id=#{shopId}
and (a.pro_name like '%${searchContent}%' or g.group_name like '%${searchContent}%')
and pg.group_id =#{groupId}
and (a.is_fenbi_change_pro = 1 and a.change_fenbi > 0)
ORDER BY a.pro_sale_total+a.sales_base desc
ORDER BY a.change_fenbi desc
ORDER BY a.id desc
limit #{firstNum},#{maxNum}
) a LEFT JOIN (SELECT product_id,specifica_img_id,inv_num,inv_price FROM t_mall_product_inventory where is_default=1 AND is_delete=0) d ON a.id=d.product_id
ORDER BY IF(d.inv_price>0 ,d.inv_price,a.pro_price )
desc
limit #{firstNum},#{maxNum}

SELECT count(DISTINCT a.id)
from t_mall_product a
left join t_mall_product_group pg on pg.product_id=a.id and pg.is_delete=0
left join t_mall_group g on g.id=pg.group_id and g.is_delete=0
WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0 and g.is_delete=0 and pg.is_delete=0

    and a.user_id = #{busId}


    and a.shop_id=#{shopId}


    and (a.pro_name like '%${searchContent}%' or g.group_name like '%${searchContent}%')


    and pg.group_id =#{groupId}


    and (a.is_fenbi_change_pro = 1 and a.change_fenbi > 0)

SELECT p.id AS id,d.product_detail, p.create_time,i.image_url,p.pro_name, p.pro_price ,p.pro_cost_price,p.pro_stock_total ,p.shop_id, p.is_specifica,p.is_integral_deduction,p.is_fenbi_deduction,p.is_coupons,p.is_member_discount,p.pro_restriction_num FROM t_mall_product p LEFT JOIN t_mall_image_associative i ON p.id=i.ass_id LEFT JOIN t_mall_store s ON p.shop_id = s.id LEFT JOIN t_mall_product_detail d ON d.product_id=p.id WHERE i.is_main_images=1 AND i.is_delete=0 AND p.is_delete=0 AND p.check_status=1 AND p.is_publish=1 AND i.ass_type = 1 AND s.is_delete=0
and
s.id = #{item.id,jdbcType=INTEGER}
AND p.pro_name LIKE '%${proName}%'
ORDER BY p.create_time DESC
limit #{firstNum},#{maxNum}

