mapper.select:
SELECT COUNT(DISTINCT a.id)
FROM t_mall_product a
LEFT JOIN t_mall_product_group pg ON pg.product_id=a.id  AND pg.is_delete=0
WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0 AND a.shop_id=#{shopId}
AND pg.group_id = #{groupId} and a.user_id=#{userId}

select count(p.id)
from t_mall_product p
where p.is_delete=0 and p.is_mall_show=0

    and p.user_id = #{userId,jdbcType=INTEGER}


    and p.is_publish = #{isPublish}


    and p.pro_stock_total = #{proStockTotal}


    and p.pro_name like '%${proName}%'


    and p.check_status = #{checkStatus}


    and

        p.shop_id = #{item.id,jdbcType=INTEGER}

select p.id as id, i.image_url as imageUrl,p.pro_name as proName, p.pro_price as proPrice,p.pro_stock_total as stockTotal, p.pro_sale_total as saleTotal,p.create_time as createTime, p.is_publish as isPublish , p.check_status as checkStatus, p.check_reason as checkReason,p.shop_id as shopId ,p.two_code_path as twoCodePath,p.return_day as returnDay, views_num as viewsNum, is_show_views as isShowView, is_integral_deduction,is_fenbi_deduction,p.is_specifica, p.sales_base,p.erp_pro_id,p.erp_inv_id,s.wx_shop_id as wxShopId from t_mall_product p left join t_mall_image_associative i on p.id=i.ass_id and i.is_main_images=1 and i.is_delete=0 and i.ass_type = 1 left join t_mall_store s on p.shop_id = s.id where p.is_delete=0 and p.is_mall_show=0
and p.user_id = #{userId,jdbcType=INTEGER}
and p.is_publish = #{isPublish}
and p.pro_stock_total = #{proStockTotal}
and p.pro_name like '%${proName}%'
and p.check_status = #{checkStatus}
and
p.shop_id = #{item.id,jdbcType=INTEGER}
order by p.create_time desc limit #{firstNum},#{maxNum}

select p.id as id, i.image_url,p.pro_name, p.pro_price ,p.pro_cost_price,p.pro_stock_total ,p.shop_id, p.is_specifica,p.is_integral_deduction,p.is_fenbi_deduction,p.is_coupons,p.is_member_discount,p.pro_restriction_num from t_mall_product p left join t_mall_image_associative i on p.id=i.ass_id left join t_mall_store s on p.shop_id = s.id where i.is_main_images=1 and i.is_delete=0 and p.is_delete=0 and p.check_status=1 and p.is_publish=1 and i.ass_type = 1 and s.is_delete=0
and s.wx_shop_id=#{wxShopId}
order by p.create_time desc
limit #{firstNum},#{maxNum}

select p.id as id, i.image_url,p.pro_name, p.pro_price ,p.pro_cost_price,p.pro_stock_total ,p.shop_id, p.is_specifica,p.is_integral_deduction,p.is_fenbi_deduction,p.is_coupons,p.is_member_discount,p.pro_restriction_num from t_mall_product p left join t_mall_image_associative i on p.id=i.ass_id left join t_mall_store s on p.shop_id = s.id where i.is_main_images=1 and i.is_delete=0 and p.is_delete=0 and p.check_status=1 and p.is_publish=1 and i.ass_type = 1 and s.is_delete=0
and s.wx_shop_id=#{wxShopId}
and p.id=#{productId}
order by p.create_time desc
limit #{firstNum},#{maxNum}

SELECT distinct(a.id),a.pro_price,a.pro_name,a.is_specifica,a.is_specifica,c.image_url,e.specifica_img_url,d.inv_num,d.inv_price,d.specifica_img_id from t_mall_product a LEFT JOIN (SELECT ass_id,image_url from t_mall_image_associative WHERE ass_type=1 AND is_delete=0 AND is_main_images=1) c ON a.id=c.ass_id LEFT JOIN (SELECT product_id,specifica_img_id,inv_num,inv_price FROM t_mall_product_inventory where is_default=1 AND is_delete=0)d ON a.id=d.product_id LEFT JOIN t_mall_product_specifica e ON d.specifica_img_id=e.id AND e.`is_delete`=0 WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0
and a.shop_id=#{shopId}
and a.pro_name like '%${proName}%'
and a.id in (select product_id from t_mall_product_group where group_id = #{groupId})
limit #{firstNum},#{maxNum}

SELECT count(DISTINCT a.id) from t_mall_product a left join t_mall_product_group pg on pg.product_id=a.id and pg.is_delete=0 left join t_mall_group g on g.id=pg.group_id and g.is_delete=0 WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0 and g.is_delete=0 and pg.is_delete=0
and a.shop_id=#{shopId}
and (a.pro_name like '%${proName}%' or g.group_name like '%${proName}%')
and pg.group_id =#{groupId}
and (a.is_integral_change_pro = 1 and a.change_integral > 0)
and (a.is_fenbi_change_pro = 1 and a.change_fenbi > 0)
and
a.id = #{item,jdbcType=INTEGER}

SELECT p.id AS id,d.product_detail, p.create_time,i.image_url,p.pro_name, p.pro_price ,p.pro_cost_price,p.pro_stock_total ,p.shop_id, p.is_specifica,p.is_integral_deduction,p.is_fenbi_deduction,p.is_coupons,p.is_member_discount,p.pro_restriction_num FROM t_mall_product p LEFT JOIN t_mall_image_associative i ON p.id=i.ass_id LEFT JOIN t_mall_store s ON p.shop_id = s.id LEFT JOIN t_mall_product_detail d ON d.product_id=p.id WHERE i.is_main_images=1 AND i.is_delete=0 AND p.is_delete=0 AND p.check_status=1 AND p.is_publish=1 AND i.ass_type = 1 AND s.is_delete=0 AND s.id IN
#{item,jdbcType=INTEGER}
AND p.pro_name LIKE '%${proName}%'
ORDER BY p.create_time DESC
limit #{firstNum},#{maxNum}
