mapper.select:
SELECT distinct(a.group_id) as group_id,b.group_name,b.is_child FROM t_mall_product_group a LEFT JOIN t_mall_group b ON a.group_id=b.id
where a.is_delete=0 AND b.is_delete=0 AND a.shop_id= #{shopId}
LEFT JOIN t_mall_store s ON s.id=a.`shop_id` WHERE a.is_delete=0 AND b.is_delete=0 AND s.`is_delete`=0
and s.wx_shop_id in
#{item.id,jdbcType=INTEGER}
and b.is_first_parents=1 order by b.sort,b.id desc

SELECT DISTINCT(a.group_id) AS group_id,b.group_name,b.is_child,a.shop_id FROM t_mall_product_group a LEFT JOIN t_mall_group b ON a.group_id=b.id WHERE a.is_delete=0 AND b.is_delete=0
AND a.shop_id= #{shopId}
AND b.is_first_parents=1
and b.group_p_id=#{classId}
ORDER BY b.sort,b.id DESC
