select distinct p.id as id, i.image_url as imageUrl,p.pro_name as proName,p.pro_price,p.pro_stock_total as stockTotal, p.pro_sale_total as saleTotal,p.create_time as createTime, p.is_publish as isPublish , p.check_status as checkStatus, p.check_reason as checkReason,p.shop_id as shopId,p.two_code_path as twoCodePath from t_mall_product_inventory inv right join t_mall_product p on inv.product_id = p.id left join t_mall_image_associative i on p.id=i.ass_id left join t_mall_store s on p.shop_id = s.id where i.is_main_images=1 and( inv.inv_num = 0 or p.pro_stock_total=0 ) and i.is_delete=0 and i.ass_type = 1 and p.is_delete = 0 and p.is_mall_show=0
and p.user_id = #{userId,jdbcType=INTEGER}
and p.is_publish = #{isPublish}
and p.pro_stock_total = #{proStockTotal}
and p.pro_name like '%${proName}%'
and
p.shop_id = #{item.id,jdbcType=INTEGER}
order by p.create_time desc limit #{firstNum},#{maxNum}

