mapper.select:
SELECT id,pro_spec_str
FROM t_mall_shop_cart



            AND product_id=#{productId}


            AND shop_id=#{shopId}


            AND product_specificas=#{productSpecificas}


            AND user_id=#{userId}


            and pro_type=#{proType}


            AND bus_user_id=#{busUserId}

SELECT a.id,a.`product_id`,a.`shop_id`,a.`product_specificas`,a.`product_num`,a.`product_speciname`,a.`price`,a.`primary_price`,a.`discount`,a.`pro_spec_str`, a.`pro_type`,a.`sale_member_id`,a.`commission`, e.image_url,d.is_specifica, d.pro_name,d.return_day,d.is_coupons,d.is_member_discount,d.pro_weight,d.is_integral_deduction,d.is_fenbi_deduction,d.pro_type_id FROM t_mall_shop_cart a LEFT JOIN t_mall_store b ON a.shop_id=b.id
LEFT JOIN t_mall_product d ON a.product_id=d.id LEFT JOIN (SELECT id,image_url,ass_id FROM t_mall_image_associative WHERE ass_type=1 AND is_delete=0 AND is_main_images=1)e ON a.product_id=e.ass_id WHERE b.is_delete=0 and a.is_check=1 and d.is_delete=0 and d.is_publish=1 and d.check_status=1
and a.id in
#{item,jdbcType=INTEGER}
and a.shop_id=#{shopId}
and a.pro_type = 0
and (a.pro_type = 1 or a.pro_type = 2)
and a.bus_user_id=#{busUserId}
group by a.id order by a.shop_id

SELECT DISTINCT(a.shop_id) as shop_id,b.wx_shop_id,b.`sto_name` , b.sto_picture AS stoPicture FROM t_mall_shop_cart a LEFT JOIN t_mall_store b ON a.shop_id=b.id WHERE b.is_delete=0
and a.is_check=1
and a.user_id in
#{item,jdbcType=INTEGER}
and a.id in
#{item,jdbcType=INTEGER}
and a.bus_user_id=#{busUserId}

mapper.update:
update t_mall_shop_cart set is_check=0


        user_id in

            #{item,jdbcType=INTEGER}

mapper.select:
SELECT IFNULL(SUM(a.`product_num`),0) AS proNum
FROM t_mall_shop_cart a
LEFT JOIN t_mall_store b ON a.shop_id=b.id
WHERE b.is_delete=0 and a.pro_type = 0

    and a.bus_user_id=#{busUserId}


    and a.user_id in

        #{item,jdbcType=INTEGER}



    and a.id in

        #{item}

SELECT a.id,a.`product_id`,a.`shop_id`,a.`product_specificas`,a.`product_num`,a.`product_speciname`,a.`price`,a.`primary_price`,a.user_type,a.`pro_spec_str`,a.`discount`, a.`pro_type`,a.`sale_member_id`,a.`commission`, b.sto_pid,e.image_url,f.id AS pageid , d.return_day,d.pro_name,d.is_specifica AS isSpec,d.is_coupons as isCoupons,d.is_member_discount ,d.pro_type_id,d.is_delete AS isDelete,d.is_publish,d.check_status,d.pro_stock_total AS pro_stock_num,d.pro_price,d.pro_stock_total AS stockNum,d.pro_restriction_num, d.is_integral_deduction ,d.is_fenbi_deduction,d.pro_weight,d.flow_id as flowId, b.wx_shop_id,b.sto_name FROM ( SELECT a.id,a.`product_id`,a.`shop_id`,a.`product_specificas`,a.`product_num`,a.`product_speciname`,a.`price`,a.`primary_price`,a.user_type,a.`pro_spec_str`,a.`discount`, a.`pro_type`,a.`sale_member_id`,a.`commission` FROM t_mall_shop_cart a
and a.user_id in
#{item,jdbcType=INTEGER}
and a.user_id = #{memberId}
and a.id in
#{item,jdbcType=INTEGER}
and a.bus_user_id= #{userId}
and a.pro_type = 0
and (a.pro_type = 1 or a.pro_type = 2)
a.shop_id = #{shopId}
GROUP BY a.id ORDER BY a.shop_id ) a LEFT JOIN t_mall_store b ON a.shop_id=b.id LEFT JOIN t_mall_product d ON a.product_id=d.id LEFT JOIN (SELECT id,image_url,ass_id FROM t_mall_image_associative WHERE ass_type=1 AND is_delete=0 AND is_main_images=1)e ON a.product_id=e.ass_id LEFT JOIN (SELECT id,pag_sto_id FROM t_mall_page WHERE pag_is_main=1) f ON a.shop_id=f.pag_sto_id WHERE b.is_delete=0

mapper.update:
UPDATE t_mall_shop_cart SET user_id =#{memberId} ,bus_user_id=#{busUserId}

    id in

        #{item,jdbcType=INTEGER}

mapper.select:
SELECT a.id,a.`product_id`,a.`shop_id`,a.`product_specificas`,a.`product_num`,a.`product_speciname`,a.`price`,a.`primary_price`,a.user_type,a.`pro_spec_str`, a.`discount`, a.`pro_type`,a.`sale_member_id`,a.`commission`, e.image_url, d.return_day,d.pro_name,d.is_specifica AS isSpec, d.is_coupons AS isCoupons,d.is_member_discount ,d.pro_type_id,d.is_delete AS isDelete,d.is_publish,d.check_status,d.pro_stock_total AS pro_stock_num, d.pro_price,d.pro_stock_total AS stockNum,d.pro_restriction_num, d.is_integral_deduction ,d.is_fenbi_deduction,d.pro_weight,d.flow_id AS flowId FROM ( SELECT distinct a.id,a.`product_id`,a.`shop_id`,a.`product_specificas`,a.`product_num`,a.`product_speciname`,a.`price`,a.`primary_price`,a.user_type,a.`pro_spec_str`,a.`discount`, a.`pro_type`,a.`sale_member_id`,a.`commission` FROM t_mall_shop_cart a
and a.id = #{id}
and a.id = #{checkIds[0] }
and a.id in
#{item,jdbcType=INTEGER}
and a.shop_id = #{shopIds}
and a.shop_id in
#{item,jdbcType=INTEGER}
and a.bus_user_id= #{userId}
and a.pro_type = 0
and (a.pro_type = 1 or a.pro_type = 2)
GROUP BY a.id ORDER BY a.shop_id ) a LEFT JOIN t_mall_product d ON a.product_id=d.id LEFT JOIN (SELECT id,image_url,ass_id FROM t_mall_image_associative WHERE ass_type=1 AND is_delete=0 AND is_main_images=1)e ON a.product_id=e.ass_id
