mapper.select:
SELECT distinct(a.id),a.pro_price,a.pro_name,a.is_specifica,c.image_url,e.specifica_img_url,d.inv_price, if(((gb.s_start_time <= now() and now() < gb.s_end_time) or (gb.s_start_time > now() and gb.s_end_time > now())) and gb.is_delete = 0 and gb.is_use = 1
and a.id != #{defaultProId} ,1,-1) as groupStatus,a.create_time as createTime,d.id as invenId from t_mall_product a LEFT JOIN (SELECT ass_id,image_url from t_mall_image_associative WHERE ass_type=1 AND is_delete=0 AND is_main_images=1) c ON a.id=c.ass_id LEFT JOIN (SELECT product_id,specifica_img_id,inv_num,inv_price,id FROM t_mall_product_inventory where is_default=1 AND is_delete=0)d ON a.id=d.product_id LEFT JOIN t_mall_product_specifica e ON d.specifica_img_id=e.id LEFT JOIN t_mall_seckill gb on gb.product_id = a.id and ((gb.s_start_time <= now() and now() < gb.s_end_time) or (gb.s_start_time > now() and gb.s_end_time > now())) and gb.is_delete = 0 and gb.is_use = 1 WHERE a.is_publish=1 AND a.check_status=1 AND a.is_delete=0
AND a.shop_id = #{shopId,jdbcType=INTEGER}
AND a.pro_name like '%${proName}%'
AND a.id in (select product_id from t_mall_product_group where group_id = #{groupId})
order by a.id desc limit #{firstNum},#{maxNum}

SELECT 
from t_mall_seckill  g
left join t_mall_seckill_join j on g.id=j.seckill_id
WHERE g.is_delete = 0 and g.is_use  =1

    and g.product_id = #{productId}

and ((g.s_start_time <= now() and now() < g.s_end_time ) or
(
g.s_start_time > now() and  g.s_end_time > now())

    and g.id != #{id}

) and j.id>0

SELECT a.id,a.is_member_discount,a.pro_price,a.pro_name,a.is_specifica,a.is_specifica,c.image_url,d.specifica_img_id,e.specifica_img_url,d.inv_num,d.inv_price, gb.id as gBuyId,gb.s_price as price ,gb.s_start_time as startTime ,gb.s_end_time as endTime,a.shop_id as shopId,d.id as inv_id, a.return_day, gb.s_max_buy_num as maxNum,a.pro_stock_total as stockTotal from t_mall_seckill gb left join t_mall_product a on a.id = gb.product_id and gb.shop_id = a.shop_id LEFT JOIN (SELECT ass_id,image_url from t_mall_image_associative WHERE ass_type=1 AND is_delete=0 AND is_main_images=1) c ON a.id=c.ass_id LEFT JOIN (SELECT product_id,specifica_img_id,inv_num,inv_price,id FROM t_mall_product_inventory where is_default=1 AND is_delete=0)d ON a.id=d.product_id LEFT JOIN t_mall_product_specifica e ON d.specifica_img_id=e.id left join t_mall_product_group pg on pg.product_id=a.id and pg.is_delete=0 left join t_mall_group g on g.id=pg.group_id and g.is_delete=0 WHERE gb.is_delete=0 and gb.is_use=1
and a.is_publish=1 AND a.check_status=1 AND a.is_delete=0
and ((gb.s_start_time <= now() and now() < gb.s_end_time ) or (gb.s_start_time > now() and gb.s_end_time > now()))
and gb.id=#{id,jdbcType=INTEGER}
and gb.shop_id=#{shopId,jdbcType=INTEGER}
and (a.pro_name like CONCAT("%","${proName}","%") or g.group_name like CONCAT("%","${proName}","%") )
and gb.product_id=#{productId,jdbcType=INTEGER}
and pg.group_id = #{groupId,jdbcType=INTEGER}
GROUP BY a.id
order by gb.id
order by a.pro_sale_total+a.sales_base
order by gb.s_price
desc

SELECT count(gb.id) from t_mall_seckill gb left join t_mall_product a on a.id = gb.product_id and gb.shop_id = a.shop_id WHERE gb.is_delete=0 and gb.is_use=1
and a.is_publish=1 AND a.check_status=1 AND a.is_delete=0
and ((gb.s_start_time <= now() and now() < gb.s_end_time ) or (gb.s_start_time > now() and gb.s_end_time > now()))
and gb.product_id=#{productId,jdbcType=INTEGER}
and gb.shop_id=#{shopId,jdbcType=INTEGER}
