mapper.select:
select count(jp.id) from t_mall_seller_join_product jp left join t_mall_product p on p.id=jp.product_id where p.is_publish=1 AND p.check_status=1 AND p.is_delete=0 and jp.is_delete=0
and
p.shop_id = #{item.id,jdbcType=INTEGER}

 /* todo 调用小屁孩门店接口 */
 SELECT  p.pro_name,jp.id,jp.commission_type,jp.commission_rate,jp.product_id,jp.shop_id,
 /*IF(ws.business_name IS NOT NULL ,ws.business_name, s.sto_name) AS shopName,*/
 jp.is_use,jp.create_time,p.two_code_path
 FROM t_mall_seller_join_product jp
 LEFT JOIN t_mall_store s ON jp.shop_id = s.id
/* LEFT JOIN t_wx_shop ws ON s.wx_shop_id = ws.id*/
 LEFT JOIN t_mall_product p ON p.id= jp.product_id
 WHERE p.is_publish=1 AND p.check_status=1 AND p.is_delete=0  AND jp.is_delete=0

     and

         p.shop_id = #{item.id,jdbcType=INTEGER}


 group by jp.id
 order by jp.create_time desc
 limit #{firstNum},#{maxNum}

SELECT

FROM `t_mall_seller_join_product` jp WHERE is_delete = 0

    and shop_id = #{shopId}


    and product_id = #{productId}


    and user_id = #{userId}

select count(DISTINCT a.id) from t_mall_seller_join_product jp left join t_mall_product a on a.id=jp.product_id LEFT JOIN (SELECT ass_id,image_url FROM t_mall_image_associative WHERE ass_type=1 AND is_delete=0 AND is_main_images=1) c ON a.id=c.ass_id LEFT JOIN (SELECT product_id,specifica_img_id,inv_num,inv_price FROM t_mall_product_inventory WHERE is_default=1 AND is_delete=0)d ON a.id=d.product_id LEFT JOIN t_mall_product_specifica e ON d.specifica_img_id=e.id AND e.is_delete=0 LEFT JOIN t_mall_product_group pg ON pg.product_id=a.id AND pg.is_delete=0 LEFT JOIN t_mall_group g ON g.id=pg.group_id AND g.is_delete=0 where jp.user_id = #{busUserId,jdbcType=INTEGER} and jp.is_delete=0 and jp.is_use=1 AND a.is_publish=1 AND a.check_status=1 AND a.is_delete=0 AND g.is_delete=0 AND pg.is_delete=0
and a.shop_id=#{shopId}
and (a.pro_name like '%${proName}%' or g.group_name like '%${proName}%')
and pg.group_id =#{groupId}
and (a.is_integral_change_pro = 1 and a.change_integral > 0)
and (a.is_fenbi_change_pro = 1 and a.change_fenbi > 0)
