mapper.select:
SELECT DISTINCT so.id,d.`det_pro_name` AS proName,o.`buyer_user_id`,o.`create_time`,o.`order_status`,d.status,o.`order_money`,o.`id` FROM `t_mall_seller_order` so LEFT JOIN t_mall_order o ON o.id=so.`order_id` LEFT JOIN t_mall_order_detail d ON d.order_id=o.`id` left join t_mall_seller s on s.member_id=so.sale_member_id WHERE so.is_delete=0
and so.sale_member_id=#{memberId}
and
so.sale_member_id = #{item,jdbcType=INTEGER}
and o.`order_status` = #{status}
and so.sale_member_id = #{custId}
and (s.check_status = 1 or s.check_status = 0 or s.check_status = -2)
and (s.check_status = 1 or s.check_status = -2)
and s.check_status = 1
GROUP BY so.id ORDER BY o.`create_time` DESC

select

from t_mall_seller_order

    1=1

        and sale_member_id = #{saleMemberId}


        and order_id = #{orderId}


        and order_detail_id = #{orderDetailId}

    limit 1

SELECT s.id, s.`member_id`, s.`referees_member_id`, s.`user_name`, s.`company_name`, s.`telephone`, IFNULL(SUM(t.`product_price`), 0) sale_money FROM t_mall_seller s LEFT JOIN t_mall_seller_order t ON t.`sale_member_id` = s.`member_id` AND t.`sale_member_id` > 0
AND t.`order_time` >= #{startTime}
and t.order_time <= #{endTime}
GROUP BY s.`member_id` ORDER BY SUM(t.`order_money`) DESC
limit #{firstNum},#{maxNum}
