mapper.select:
select count(o.id) from t_mall_store o
o.is_delete = 0
and o.sto_user_id=#{userId}
and t.sto_name like '%${stoName}%'
AND o.`wx_shop_id` in
#{item}

mapper.update:
update t_mall_store set is_delete = 1

    id in

        #{item}

mapper.select:
select s.sto_picture as stoPicture ,s.id,mp.id as pageId from t_mall_store s left join t_mall_page mp on mp.pag_sto_id=s.id and pag_is_main=1
s.`is_delete` and s.`sto_user_id`=#{userId}
order by mp.id desc
